sp_widget.client_script:
api.controller = function() {
    /* widget controller */
    var c = this;
    c.uiAction = function(action) {
        c.data.action = action;
        c.server.update().then(function() {
            c.data.action = undefined;
        });
    };
};

sp_widget.css:
.wrap { 
  	margin: 0 auto 8rem auto;
  	//padding-bottom: 15px;
  	display: flex; 
		flex-flow: row ; 
  	width: 30%;
}   

.wrap_item { 
		flex: 1;
}

